import React, { Component } from "react";
import ToDo from "./components/ToDo/ToDo";
import "./App.css"

export default class App extends Component {
  state = {
    reminders: [],
    newReminder: "",
  };

  changeNewReminderContent = (event) => {
    console.log(this.state.newReminder.length);
    const changedContent = event.target.value;
    this.setState({ newReminder: changedContent });
  };

  addNewReminder = () => {
    const newReminder = this.state.newReminder;
    this.setState((previousState) => ({
      reminders: [...previousState.reminders, newReminder],
    }));
    this.setState({ newReminder: "" });
  };

  removeReminder = (reminderIndex) => {
    const reminders = this.state.reminders;
    reminders.splice(reminderIndex, 1);
    this.setState({ reminders: reminders });
  };

  render() {
    const mainStyle={


    }
    return (
      <div className="main" style={mainStyle}>
        <input
          type="text"
          onChange={(e) => this.changeNewReminderContent(e)}
          value={this.state.newReminder}
        />
        <button onClick={this.addNewReminder}>Add new Reminder</button>
        {this.state.reminders.map((item, index) => {
          return <ToDo task={item} click={() => setTimeout(() => this.removeReminder(index),700)} />;
        })}
      </div>
    );
  }
}
