import "./App.css";
import React, { useState } from "react";
import { useFormik } from "formik";

class App extends React.Component {
  state = {
    name: "",
    password: "",
    showingPassword: ""
  };

  passwordHandler = (event) => {
    this.setState({password: event.target.value})
    var hiddenPass = '';
    for(var i=0; i < 0.5*this.state.password.length; ++i){
      hiddenPass += "*";
    }
    this.setState({showingPassword: hiddenPass})
  };


  render() {
    const textColor = {
      color: this.state.color,
    };
    return (
      <div className="App">
        <form onSubmit={this.changeColor}>
          <label>Sign Up</label>
          <br></br>
          <p>Username: </p><br></br>
          <input type="text"/><br></br>
          <p>Password: </p><br></br>
          <input type="text" onChange={this.passwordHandler} value={this.state.showingPassword}/>
          <br></br>
          <input type="submit" value="Submit" />
        </form>
        <p>your name is:</p>
        <p style={textColor}>{this.state.name}</p>
      </div>
    );
  }
}

export default App;
