import React, { Component } from "react";
import ToDo from "./components/ToDo/ToDo";
import "./App.css";
import Completed from "./components/Completed/Completed";

let lastGeneratedKey = 0;

export default class App extends Component {
  state = {
    reminders: [],
    allReminders: [],
    completedReminders: [],
    unCompletedReminders: [],
    all: true,
    completed: false,
    uncompleted: false,
    newReminder: "",
  };

  showingReminders = () => {
    let arr;
    if (this.state.all) {
      arr = this.state.allReminders;
      this.setState({ reminders: arr });
    }
    if (this.state.completed) {
      arr = this.state.completedReminders;
      this.setState({ reminders: arr });
    }
    if (this.state.uncompleted) {
      arr = this.state.unCompletedReminders;
      this.setState({ reminders: arr });
    }
  };

  changeNewReminderContent = (event) => {
    const changedContent = event.target.value;
    this.setState({ newReminder: changedContent });
  };

  addNewReminder = () => {
    const newReminder = { status: "uncompleted", task: this.state.newReminder };
    this.setState(
      (previousState) => ({
        allReminders: [newReminder, ...previousState.allReminders],
        uncompletedReminders: [
          newReminder,
          ...previousState.uncompletedReminders,
        ],
        newReminder: "",
      }),
      this.showingReminders()
    );
  };

  removeReminder = (reminderIndex) => {
    const reminders = this.state.reminders;
    reminders[reminderIndex].status = "completed";
    this.setState({ reminders: reminders });
  };

  showAllReminders = () => {
    const arr = this.state.allReminders;
    this.setState({ reminders: arr });
    this.setState({ all: true }, { completed: false }, { uncompleted: false });
  };

  showCompletedReminders = () => {
    const arr = this.state.completedReminders;
    this.setState({ reminders: arr });
    this.setState({ all: false }, { completed: true }, { uncompleted: false });
  };

  showUncompletedReminders = () => {
    const arr = this.state.unCompletedReminders;
    this.setState({ reminders: arr });
    this.setState({ all: false }, { completed: false }, { uncompleted: true });
  };

  render() {
    let mainStyle = {
      height: "130px",
    };

    if (this.state.reminders.length >= 2) {
      mainStyle = {
        height: 160 + 21.5 * (this.state.reminders.length - 1),
      };
    }
    return (
      <div className="main" style={mainStyle}>
        <input
          type="text"
          className="Task"
          onChange={(e) => this.changeNewReminderContent(e)}
          value={this.state.newReminder}
          placeholder="Enter Your New Task"
        />
        <input
          className="Task"
          type="button"
          onClick={this.addNewReminder}
          value="Add"
        />
        {this.state.reminders.map((item, index) => {
          if (item.status === "completed") {
            return <Completed key={lastGeneratedKey++} task={item.task} />;
          }
          return (
            <ToDo
              task={item.task}
              click={() => setTimeout(() => this.removeReminder(index), 700)}
              key={lastGeneratedKey++}
            />
          );
        })}
        <button className="all" onClick={this.showAllReminders}>
          All
        </button>
        <button className="completed" onClick={this.showCompletedReminders}>
          Completed
        </button>
        <button className="uncompleted" onClick={this.showUncompletedReminders}>
          Uncompleted
        </button>
      </div>
    );
  }
}
