import React, { Component } from "react";
import ToDo from "./components/ToDo/ToDo";
import Completed from "./components/Completed/Completed";
import "./App.css";

let lastGeneratedKey = 0;


export default class App extends Component {
  state = {
    reminders: [],
    allReminders: [],
    completedReminders: [],
    unCompletedReminders: [],
    all: true,
    completed: false,
    uncompleted: false,
    newReminder: "",
  };
  removeReminderFromList = (array, id) => {
    for(var i=0 ; i<array.length ; i++){
      if(array[i].ID == id){
        array.splice(i,1)
      }
    }
    return array;
  }

  showingReminders = () => {
    let arr = [];
    if (this.state.all) {
      arr = this.state.allReminders;
    } else if (this.state.completed) {
      arr = this.state.completedReminders;
    } else if (this.state.uncompleted) {
      arr = this.state.unCompletedReminders;
    }
    this.setState({ reminders: arr });
  };

  changeNewReminderContent = (event) => {
    this.setState({ newReminder: event.target.value });
  };

  addNewReminder = () => {
    const newReminder = { status: "uncompleted", task: this.state.newReminder, ID: lastGeneratedKey++ };
    this.setState((prevState) => {
      const allReminders = [newReminder, ...prevState.allReminders];
      const unCompletedReminders = [newReminder, ...prevState.unCompletedReminders];
      return { allReminders, unCompletedReminders, newReminder: "" };
    }, this.showingReminders);
  };

  markReminderAsCompleted = (reminderIndex) => {
    this.setState((prevState) => {
      const allReminders = [...prevState.allReminders];
      allReminders[reminderIndex].status = "completed";
      const completedReminders = allReminders.filter(reminder => reminder.status === "completed");
      const unCompletedReminders = allReminders.filter(reminder => reminder.status === "uncompleted");
      return { allReminders, completedReminders, unCompletedReminders };
    }, this.showingReminders);
  };

  showAllReminders = () => {
    this.setState({ all: true, completed: false, uncompleted: false }, this.showingReminders);
  };

  showCompletedReminders = () => {
    this.setState({ all: false, completed: true, uncompleted: false }, this.showingReminders);
  };

  showUncompletedReminders = () => {
    this.setState({ all: false, completed: false, uncompleted: true }, this.showingReminders);
  };

  render() {
    let mainStyle = {
      height: "160px",
    };

    if (this.state.reminders.length >= 1) {
      mainStyle = {
        height: 160 + 21.5 * (this.state.reminders.length),
      };
    }

    return (
      <div className="main" style={mainStyle}>
        <input
          type="text"
          className="Task"
          onChange={this.changeNewReminderContent}
          value={this.state.newReminder}
          placeholder="Enter Your New Task"
        />
        <input
          className="addButton"
          type="button"
          onClick={this.addNewReminder}
          value="Add"
        />
        {this.state.reminders.map((item) => {
          if (item.status === "completed") {
            return <Completed key={item.ID} task={item.task} click={() => this.removeElement(this.state.reminders.indexOf(item))} />;
          }
          return (
            <ToDo
              key={item.ID}
              task={item.task}
              click={() => setTimeout(() => this.markReminderAsCompleted(this.state.reminders.indexOf(item)), 500)}
            />
          );
        })}
        <button className="all" onClick={this.showAllReminders}>
          All
        </button>
        <button className="completed" onClick={this.showCompletedReminders}>
          Completed
        </button>
        <button className="uncompleted" onClick={this.showUncompletedReminders}>
          Uncompleted
        </button>
      </div>
    );
  }
}
