import React, { Component } from "react";
import ToDo from "./components/ToDo/ToDo";
import Completed from "./components/Completed/Completed";
import "./App.css";

let lastGeneratedKey = 0;

function serchForElement(array, key){
  for(var i=0; i<array.length ; i++){
    if(array[i].key === key){
      array.splice(i,1)
    }
  }
  return array;
}

export default class App extends Component {
  state = {
    reminders: [],
    allReminders: [],
    completedReminders: [],
    unCompletedReminders: [],
    all: true,
    completed: false,
    uncompleted: false,
    newReminder: "",
  };

  showingReminders = () => {
    let arr = [];
    if (this.state.all) {
      arr = this.state.allReminders;
    } else if (this.state.completed) {
      arr = this.state.completedReminders;
    } else if (this.state.uncompleted) {
      arr = this.state.unCompletedReminders;
    }
    this.setState({ reminders: arr });
  };

  changeNewReminderContent = (event) => {
    const changedContent = event.target.value;
    this.setState({ newReminder: changedContent });
  };

  addNewReminder = () => {
    const newReminder = { status: "uncompleted", task: this.state.newReminder, key: lastGeneratedKey++ };
    this.setState((previousState) => ({
      allReminders: [newReminder, ...previousState.allReminders],
      unCompletedReminders: [newReminder, ...previousState.unCompletedReminders],
      newReminder: ""
    }), this.showingReminders);
  };

  removeReminder = (reminderIndex) => {
    this.setState((previousState) => {
      const allReminders = [...previousState.allReminders];
      allReminders[reminderIndex].status = "completed";
      const completedReminders = allReminders.filter(reminder => reminder.status === "completed");
      const unCompletedReminders = allReminders.filter(reminder => reminder.status === "uncompleted");
      return { allReminders, completedReminders, unCompletedReminders };
    }, this.showingReminders);
  };

  showAllReminders = () => {
    this.setState({ reminders: this.state.allReminders, all: true, completed: false, uncompleted: false });
  };

  showCompletedReminders = () => {
    this.setState({ reminders: this.state.completedReminders, all: false, completed: true, uncompleted: false });
  };

  showUncompletedReminders = () => {
    this.setState({ reminders: this.state.unCompletedReminders, all: false, completed: false, uncompleted: true });
  };

  removeElement = (key) => {
    let allReminders = this.state.allReminders
    let completedReminders = this.state.completedReminders
    serchForElement(allReminders,key)
    serchForElement()
    this.showingReminders()
  }

  render() {
    let mainStyle = {
      height: "160px",
    };

    if (this.state.reminders.length >= 1) {
      mainStyle = {
        height: 160 + 21.5 * (this.state.reminders.length),
      };
    }
    return (
      <div className="main" style={mainStyle}>
        <input
          type="text"
          className="Task"
          onChange={this.changeNewReminderContent}
          value={this.state.newReminder}
          placeholder="Enter Your New Task"
        />
        <input
          className="addButton"
          type="button"
          onClick={this.addNewReminder}
          value="Add"
        />
        {this.state.reminders.map((item, index) => {
          if (item.status === "completed") {
            return <Completed task={item.task} onClick={() => this.removeElement(index)}/>;
          }
          return (
            <ToDo
              task={item.task}
              click={() => setTimeout(() => this.removeReminder(this._reactInternalFiber.key), 500)}
            />
          );
        })}
        <button className="all" onClick={this.showAllReminders}>
          All
        </button>
        <button className="completed" onClick={this.showCompletedReminders}>
          Completed
        </button>
        <button className="uncompleted" onClick={this.showUncompletedReminders}>
          Uncompleted
        </button>
      </div>
    );
  }
}
