import React, { Component } from 'react'

export default class App extends Component {
  state={
    reminders: [],
    newReminder: '',
  }

  changeNewReminderContent = (event) => {
    const changedContent = event.target.value
    this.setState({newReminder: changedContent})
  }

  addNewReminder = () => {
    const newReminder = this.state.newReminder
    const prevReminders = this.state.reminders
    prevReminders.push(newReminder)
  }

  render() {
    return (
      <div>
        <input type='text' onChange={(e) => this.changeNewReminderContent(e)} /><br></br>
        <button onClick={this.addNewReminder}></button>
      </div>
    )
  }
}
